mapper.select:
select 

from dept_emp
where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}

mapper.delete:
delete from dept_emp
where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}

mapper.update:
update dept_emp


    from_date = #{fromDate,jdbcType=DATE},


    to_date = #{toDate,jdbcType=DATE},


where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}

update dept_emp
set from_date = #{fromDate,jdbcType=DATE},
  to_date = #{toDate,jdbcType=DATE}
where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}
